function start(path) {
    var Worker = null,
    worker = null,
    workerPath = '',
    SyrupChannel = null,
    syrupChannel = null;

    if (path) {
        workerPath = path + '/scripts/worker.js';
        workerPath = workerPath.replace(/\/\//g,'/');
    } else {
        workerPath = '/designer/scripts/worker.js';
    }

    if (typeof SharedWorker !== 'undefined' && !this.require('channel-admin')) {

        SyrupChannel = this.require('SyrupChannel');
        syrupChannel = new SyrupChannel({
            '_id': 'channel-admin',
            '_core': true
        });

        syrupChannel.on('updateBehavior', function updateBehavior(id, behavior) {
            this.require(id).action(behavior.action);
        });
        syrupChannel.on('sync', function sync() {
            this.loadSystem(JSON.parse(this.require('db').system()));
        });
        syrupChannel.on('send', function send(message) {
            this.require('worker-admin').worker().port.postMessage(message);
        });

        Worker = this.require('Worker');
        worker = new Worker({
            '_id': 'worker-admin',
            '_core': true,
            'worker': new SharedWorker(workerPath),
        });

        worker.worker().port.onmessage = function (e) {
          $db.SyrupMessage.insert(e.data);
        };

        // notify System Designer
        syrupChannel.sync();

        console.info('syrup: admin is started');
    }
}

