function stop() {
    var worker = null,
    channel = null;

    worker = this.require('worker-admin');
    if (worker && worker.worker()) {
        worker.worker().port.onmessage = function () {};
        worker.destroy();

        channel = this.require('channel-admin');
        if (channel) {
            channel.destroy();
        }

        console.info('syrup: admin is stopped');
    }
}

