function off(state, behaviorId) {
    var args = [],
    i = 0,
    length = 0;
    length = arguments.length;

    for (i = 0; i < length - 6; i++) {
        args.push(arguments[i]);
    }

    if ($workflow.checkParams({
        "component": this, 
        "methodName": "off", 
        "args": args
        })) {

        if (state || behaviorId) {
            if ($metamodel.isValidState(state, this.constructor.name)) {
                $behavior.remove({
                    "behaviorId": behaviorId, 
                    "componentId": this.id(), 
                    "state": state
                });
            } else { 
                console.warn("syrup: invoke \'off\' method of component '" + this.id() + "' with an invalid state '" + state + "'"); 
            }
        } else {
            $behavior.remove({
                "componentId": this.id()
            });
        }
    }
}

