function load(url, async) {
    var xhr = null,
    callbackLoad = null;
    xhr = new XMLHttpRequest();

    callbackLoad = function callbackLoad(system) {
        var sysId = $db.system(system),
        sys = $component.get(sysId),
        systems = document.querySelectorAll('link[rel=system]'),
        nbSubsystem = $db.SyrupSystem.find({
            'subsystem': true
        }); 
        if (sys) {
            sys.main();
        } 
        if (systems.length + 1 + nbSubsystem.length === $db.SyrupSystem.count()) {
            $component.get('syrup').ready();
        }
    };

    if (async) {
        xhr.open('GET', url, true);
        xhr.onreadystatechange = function () {
            if (xhr.readyState === 4) {
                if (xhr.status === 200) {
                    callbackLoad(JSON.parse(xhr.response));
                }
            }
        };
        xhr.send(null);
    } else {
        xhr.open('GET', url, false);
        xhr.send(null);
        if (xhr.status === 200) {
            callbackLoad(JSON.parse(xhr.response));
        }
    }
}

