function main() {
    var subsystems = [],
    systems = [],
    system = null,
    i = 0,
    length = 0;

    subsystems = $db.SyrupSystem.find({
        'subsystem': true
    });
    subsystems.forEach(function (subsystem) {
        var subsystemId = subsystem._id;
        this.require(subsystemId).main();
    }, this); 

    if (typeof document !== 'undefined') {
        systems = document.querySelectorAll('link[rel=system]');
        length = systems.length;

        for (i = 0; i < length; i++) {
            system = systems[i];

            if (system.getAttribute('async') === 'false') {
                this.require('syrup').load(system.href, false);
            } else {
                this.require('syrup').load(system.href, true);
            }
        }
        if (length === 0) {
            this.require('syrup').ready();
        }
    }
}

