function off(state, behaviorId) {
    var args = [],
    i = 0,
    length = 0;
    length = arguments.length;

    for (i = 0; i < length - 7; i++) {
        args.push(arguments[i]);
    }

    if ($workflow.checkParams({
        "component": this, 
        "methodName": "off", 
        "args": args
        })) {

        if (state || behaviorId) {
            if ($metamodel.isValidState(state, this.constructor.name)) {
                $behavior.remove({
                    "behaviorId": behaviorId, 
                    "componentId": this.id(), 
                    "state": state
                });
            } else { 
                this.require('logger').warn("invoke \'off\' method of component '" + this.id() + "' with an invalid state '" + state + "'"); 
            }
        } else {
            $behavior.remove({
                "componentId": this.id()
            });
        }
    }
}
