function system(name) {
    var System = null,
    system = {},
    systemId = '',
    result = [],
    conf = {};

    if (name) {
        conf.master = true;
        conf.name = name;
        System = this.require('SyrupSystem');
        system = new System(conf);
    } else {
        result = $db.SyrupSystem.find({
            'master': true
        });
        if (result.length) {
            systemId = result[0]._id;
            system = $component.get(systemId);
        }
    }
    return system;
}
