function install(url, autoStart, async) { 
  var system = {},
      systemId = '',
      callbackLoad = null,
      xhr = null,
      result = '',
      channel = $component.get('channel');

  if (typeof url === 'object') {
    systemId = runtime.require('db').system(url); 
    if (systemId) {
      system = runtime.require(systemId);

      system.status('installed');    
      channel.$systemInstalled(systemId);
      system.status('resolved');
      channel.$systemResolved(systemId);

      if (autoStart) {
        system.status('starting');
        channel.$systemStarting(systemId);
        system.start();
        system.status('active');
        channel.$systemActive(systemId);
      }

      result = systemId;
    }
  } else {   
    if (typeof global !== 'undefined') {
      if (url.indexOf('.json') !== -1) {
        system = global.require(global.process.env.PWD + '/' + url);
      } else {
        system = global.require(url);
      }
      systemId = this.require('db').system(system);
      if (this.require(systemId).main) {
        this.require(systemId).main();
      }
      if (this.require(systemId).start) {
        this.require(systemId).start();
      }
      result = sysId;
    } else {
      xhr = new XMLHttpRequest();
      callbackLoad = function callbackLoad(system) {
        var sysId = $db.system(system),
            sys = $component.get(sysId),
            channel = $component.get('channel');

        sys.status('installed');    
        channel.$systemInstalled(sysId);
        if (!sys.master()) {
          sys.status('resolved');
          channel.$systemResolved(sysId);
        }
        if (sys && autoStart) {
          if (sys.master()) {
            sys.status('resolved');
            channel.$systemResolved(sysId);
          }
          sys.status('starting');
          channel.$systemStarting(sysId);
          if (sys.main) {
            sys.main(); // deprecated
          }
          if (sys.start) {
            sys.start();
          }
          sys.status('active');
          channel.$systemActive(sysId);
          result = sysId;
        } 
      };

      if (async) {
        xhr.open('GET', url, true);
        xhr.onreadystatechange = function () {
          if (xhr.readyState === 4) {
            if (xhr.status === 200) {
              callbackLoad(JSON.parse(xhr.response));
            }
          }
        };
        xhr.send(null);
      } else {
        xhr.open('GET', url, false);
        xhr.send(null);
        if (xhr.status === 200) {
          callbackLoad(JSON.parse(xhr.response));
        }
      }
    }
  }
  return result;
}

