function uninstall(id) { 
	var search = {},
	    system = null,
	    behaviorId = '',
	    collection =  '',
	    componentId = '',
	    length = 0,
	    i = 0;

	search = $db.RuntimeSystem.find({
	  '_id': id
	});

	if (search.length) {
	  system = search[0];
	  // remove behaviors
	  if (system.behaviors) {
	    for (behaviorId in system.behaviors) {
	      $db.RuntimeBehavior.remove({ 
	        '_id': system.behaviors[behaviorId]._id
	      });
	    }
	  }
	  // remove components
	  if (system.components) {
	    for (collection in system.behaviors) {
	      for (componentId in system.behaviors[collection]) {
	        $db[collection].remove({ 
	          '_id': componentId
	        });
	      }
	    }
	  }
	}

	this.require(id).status('uninstalled');
	channel.$systemUninstalled(id);
}

