function $systemInstalled(id) {
    var systems = null,
        dependencies = [],
        master = [],
        canStart = true;

    if (id !== 'e89c617b6b15d24') {
        if (typeof global === 'undefined' && typeof document !== 'undefined') {

            // if all systems are installed
            systems = $db.RuntimeSystem.find({});

            systems.forEach(function (system) {
                var sys = this.require(system._id);
                if (sys.status() === 'none') {
                    canStart = false;
                }
            }.bind(this));

            // start all the systems
            if (canStart) {
                dependencies = $db.RuntimeSystem.find({
                    'master': false
                });

                dependencies.forEach(function (dep) {
                    var system = this.require(dep._id);
                    channel = this.require('channel');

                    if (system.status() === 'resolved') {
                      system.status('starting');
                      channel.$systemStarting(dep._id);
                      system.start();
                      system.status('active');
                      channel.$systemActive(dep._id);
                    }
                }.bind(this));

                master = $db.RuntimeSystem.find({
                    'master': true
                });

                master.forEach(function (dep) {
                    var system = this.require(dep._id);
                    channel = this.require('channel');

                    if (system.status() === 'installed') {
                      system.status('resolved');
                      channel.$systemResolved(dep._id);
                      system.status('starting');
                      channel.$systemStarting(dep._id);
                      system.start();
                      system.status('active');
                      channel.$systemActive(dep._id);
                    }
                }.bind(this));
            }
        }
    }
}
