function $systemInstalled(id) {
  var systems = null,
    dependencies = [],
    master = [],
    canStart = true;

  if (id !== 'e89c617b6b15d24') {
    // if all systems are installed
    systems = $db._System.find({});

    systems.forEach(function (system) {
      var sys = this.require(system._id);
      if (sys.state() === 'none') {
        canStart = false;
      }
    }.bind(this));

    // start all the systems
    if (canStart) {
      dependencies = $db._System.find({
        'master': false
      });

      dependencies.forEach(function (dep) {
        var system = this.require(dep._id);
        channel = this.require('channel');

        if (system.state() === 'resolved') {
          system.state('starting');
          system.start();
          channel.$systemStarted(dep._id);
          system.state('active');
        }
      }.bind(this));

      master = $db._System.find({
        'master': true
      });

      master.forEach(function (dep) {
        var system = this.require(dep._id);
        channel = this.require('channel');

        if (system.state() === 'resolved') {
          system.state('starting');
          system.start();
          channel.$systemStarted(dep._id);
          system.state('active');
        }
      }.bind(this));
    }
  }
}
