function start() {
  var systems = [],
    system = null,
    scripts = [],
    script = null,
    logLevel = 'warn',
    i = 0,
    length = 0;

  // in a browser
  if (typeof document !== 'undefined') {
    systems = document.querySelectorAll('link[rel=system]');
    length = systems.length;

    // logger
    scripts = document.querySelectorAll('script[log]');
    if (scripts.length) {
      logLevel = scripts[0].getAttribute('log');
      this.require('logger').level(logLevel);
    }

    // systems
    for (i = 0; i < length; i++) {
      system = systems[i];

      if (system.getAttribute('async') === 'false') {
        this.require('runtime').install(system.href, false);
      } else {
        this.require('runtime').install(system.href, true);
      }
    }

    // ready event
    if (length === 0) {
      this.require('runtime').ready();
    }
  }
}
